import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import GoogleLogin from './GoogleLogin';
import auctionBackground from '../images/auction1.jpg';

const Authentication = ({ setUserRole }) => {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [selectedRole, setSelectedRole] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('client');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleRoleChange = (event) => {
    setSelectedRole(event.target.value);
  };

  const handleLogin = (event) => {
    event.preventDefault();
    const token = btoa(JSON.stringify({ username, role: selectedRole }));
    localStorage.setItem("access_token", token);
    setUserRole(selectedRole);
    navigate(`/${selectedRole}-dashboard`);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccessMessage("");

    if (!username || !email || !password) {
      setError("All fields are required.");
      setLoading(false);
      return;
    }

    const mockDatabase = [
      { username: "admin", email: "admin@example.com", password: "admin123", role: "admin" },
      { username: "auctioneer", email: "auctioneer@example.com", password: "auction123", role: "auctioneer" },
      { username: "client", email: "client@example.com", password: "client123", role: "client" },
    ];

    try {
      const existingUser = mockDatabase.find(
        (user) => user.username === username || user.email === email
      );

      if (existingUser) {
        setError("Username or email already exists.");
        setLoading(false);
        return;
      }

      const existingAdmin = mockDatabase.find((user) => user.role === "admin");

      if (role === "admin" && existingAdmin) {
        setError("An admin already exists. Only one admin is allowed.");
        setLoading(false);
        return;
      }

      mockDatabase.push({ username, email, password, role });
      localStorage.setItem("newUser", JSON.stringify({ username, email, role }));
      setSuccessMessage("Registration Successful! Redirecting to login...");
      setTimeout(() => {
        setIsLoginMode(true);
      }, 2000);
    } catch (error) {
      setError("Registration Failed! Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div 
      className="relative min-h-screen flex items-center justify-center bg-fixed bg-cover bg-center"
      style={{ backgroundImage: `url(${auctionBackground})` }}
    >
      <div className="absolute inset-0 bg-black opacity-50"></div> {/* Dark overlay */}
      <div className="relative z-10 w-full max-w-md p-8 bg-white rounded-lg shadow-xl transition-opacity duration-500 hover:bg-opacity-90">
        <h2 className="text-center text-3xl font-bold mb-6 text-gray-800">
          {isLoginMode ? 'Login' : 'Create an Account'}
        </h2>
        {error && <div className="text-red-600 text-sm mb-4">{error}</div>}
        {successMessage && <div className="text-green-600 text-sm mb-4">{successMessage}</div>}

        <form onSubmit={isLoginMode ? handleLogin : handleRegister} className="space-y-5">
          {!isLoginMode && (
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
              required
            />
          )}
          <input
            type="text"
            placeholder="Username"
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <select
            value={isLoginMode ? selectedRole : role}
            onChange={isLoginMode ? handleRoleChange : (e) => setRole(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
          >
            <option value="">{isLoginMode ? 'Login as' : 'Register as'}</option>
            <option value="admin">Admin</option>
            <option value="auctioneer">Auctioneer</option>
            <option value="client">Client</option>
          </select>
          <button
            type="submit"
            className="w-full py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition ease-in-out duration-300 focus:outline-none"
          >
            {loading ? (isLoginMode ? "Logging in..." : "Registering...") : isLoginMode ? "Login" : "Register"}
          </button>
        </form>

        <div className="mt-4 text-center text-sm">
          <span className="text-gray-500">
            {isLoginMode ? "Don't have an account?" : "Already have an account?"}
          </span>
          <button
            onClick={() => setIsLoginMode(!isLoginMode)}
            className="ml-2 text-blue-500 hover:underline focus:outline-none"
          >
            {isLoginMode ? "Register" : "Login"}
          </button>
        </div>
        {isLoginMode && (
          <div className="my-6 text-center">
            <div className="text-gray-500">or continue with:</div>
            <GoogleLogin setUserRole={setUserRole} />
          </div>
        )}
      </div>
    </div>
  );
};

export default Authentication;
