import React, { useState } from 'react';
import Navbar from './Navbar';
import AuctionList from './AuctionList';
import auctionData from '../data/AuctionData';


const HomePage = ({ userRole, handleLogout }) => {
  return (
    <div className="bg-gray-100 min-h-screen">
      {/* Navbar */}
      <Navbar userRole={userRole} handleLogout={handleLogout} />

      {/* Main content */}
      <div className="container mx-auto px-4 py-8">
        <h2 className="text-3xl font-semibold text-center mb-6">Available Auctions</h2>

        {/* Auction list in grid layout */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {auctionData.map((item) => (
            <AuctionCard key={item.id} item={item} />
          ))}
        </div>
      </div>
    </div>
  );
};

const AuctionCard = ({ item }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === item.images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? item.images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden group">
      <div className="relative">
        {/* Image Carousel */}
        <img
          src={item.images[currentImageIndex]}
          alt={item.title}
          className="w-full h-64 object-cover transition-transform duration-300 group-hover:scale-105"
        />
        <button
          onClick={handlePrevImage}
          className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-1 rounded-full focus:outline-none"
        >
          &lt;
        </button>
        <button
          onClick={handleNextImage}
          className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-1 rounded-full focus:outline-none"
        >
          &gt;
        </button>
      </div>
      <div className="p-4">
        <h3 className="text-lg font-semibold">{item.title}</h3>
        <p className="text-gray-600">{item.shortDescription}</p>
        <div className="mt-2">
          <p className="text-gray-700 font-semibold">Current Bid: ${item.currentBid}</p>
          <p className="text-gray-500 text-sm">Auction ends: {item.endDate}</p>
        </div>
        <button
          className="mt-4 w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
        >
          View Details
        </button>
      </div>
    </div>
  );
};

export default HomePage;
