import React, { useState } from 'react';

const AuctioneerDashboard = () => {
  const [itemName, setItemName] = useState('');
  const [itemDescription, setItemDescription] = useState('');
  const [itemImages, setItemImages] = useState([]);
  const [startingBid, setStartingBid] = useState('');
  const [category, setCategory] = useState('');
  const [auctionTime, setAuctionTime] = useState('');
  const [items, setItems] = useState([]); // List of auction items

  // Categories for auction items
  const categories = [
    'Motor Vehicles',
    'Electronics',
    'Land & Real Estate',
    'Furniture',
    'Art & Collectibles',
    'Jewelry & Watches',
    'Antiques',
    'Machinery',
  ];

  // Handle form submission
  const handleAddItem = (e) => {
    e.preventDefault();

    if (itemImages.length < 1 || itemImages.length > 10) {
      alert('Please upload between 1 and 10 images.');
      return;
    }

    const newItem = {
      name: itemName,
      description: itemDescription,
      images: itemImages,
      startingBid: startingBid,
      category: category,
      auctionTime: auctionTime,
      status: 'pending', // status 'pending' means awaiting admin approval
    };

    setItems([...items, newItem]);
    setItemName('');
    setItemDescription('');
    setItemImages([]);
    setStartingBid('');
    setCategory('');
    setAuctionTime('');
  };

  // Handle image selection
  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 10) {
      alert('You can upload a maximum of 10 images.');
      return;
    }
    setItemImages(files);
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-semibold">Auctioneer Dashboard</h1>

      <div className="mt-6">
        <h2 className="text-2xl font-medium">Add Item to Auction</h2>
        <form onSubmit={handleAddItem} className="mt-4">
          {/* Item Name, Description, Images, etc. */}
          <div className="mb-4">
            <label htmlFor="auctionTime" className="block text-lg font-medium">
              Auction Time
            </label>
            <input
              type="datetime-local"
              id="auctionTime"
              value={auctionTime}
              onChange={(e) => setAuctionTime(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded mt-2"
              required
            />
          </div>
          {/* Submit Button */}
          <button type="submit" className="bg-blue-600 text-white p-2 rounded">
            Submit Item for Approval
          </button>
        </form>
      </div>
    </div>
  );
};

export default AuctioneerDashboard;
