import React, { useEffect, useState } from 'react';
import './index.css';
import Navbar from './components/Navbar';
import AuctionList from './components/AuctionList';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom"; // Use Routes instead of Switch
import Registration from './components/Registration';
import Login from './components/Login';

const App = () => {
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    // Get the role from the JWT token or localStorage (this is just a placeholder)
    const token = localStorage.getItem("access_token");
    if (token) {
      const decodedToken = JSON.parse(atob(token.split(".")[1]));
      setUserRole(decodedToken.role); // Assuming role is stored in the JWT
    }
  }, []);

  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        <Navbar />
        <main className="container mx-auto p-6">
          <h1 className="text-3xl font-bold mb-6">Mnada Auctions</h1>

          <Routes> {/* Replace Switch with Routes */}
            <Route path="/register" element={<Registration />} />
            <Route path="/login" element={<Login />} />
            <Route path="/dashboard" element={
              userRole === "admin" ? <div>Admin Dashboard</div> :
              userRole === "auctioneer" ? <div>Auctioneer Dashboard</div> :
              userRole === "client" ? <div>Client Dashboard</div> :
              <div>Please login to access the dashboard</div>
            } />
            <Route path="/" element={<AuctionList />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
