import React, { useState } from 'react';
import Navbar from './Navbar';
import auctionData from '../data/AuctionData';

const HomePage = ({ userRole, handleLogout }) => {
  const [selectedAuction, setSelectedAuction] = useState(null);

  const handleSelectAuction = (item) => {
    setSelectedAuction(item);
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      {/* Navbar */}
      <Navbar userRole={userRole} handleLogout={handleLogout} />

      <div className="container mx-auto px-4 py-8">
        <h2 className="text-3xl font-semibold text-center mb-6">Available Auctions</h2>

        {!selectedAuction ? (
          // Auction grid layout
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {auctionData.slice(0, 6).map((item) => (
              <AuctionCard key={item.id} item={item} onSelect={() => handleSelectAuction(item)} />
            ))}
          </div>
        ) : (
          // Selected auction detail view with image carousel
          <AuctionDetail auction={selectedAuction} onBack={() => setSelectedAuction(null)} />
        )}
      </div>
    </div>
  );
};

const AuctionCard = ({ item, onSelect }) => (
  <div className="bg-white shadow-lg rounded-lg overflow-hidden group cursor-pointer" onClick={onSelect}>
    <img
      src={item.images[0]} // Display the first image as a thumbnail
      alt={item.title}
      className="w-full h-64 object-cover transition-transform duration-300 group-hover:scale-105"
    />
    <div className="p-4">
      <h3 className="text-lg font-semibold">{item.title}</h3>
      <p className="text-gray-600">{item.shortDescription}</p>
    </div>
  </div>
);

const AuctionDetail = ({ auction, onBack }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === auction.images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? auction.images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden max-w-2xl mx-auto">
      <button onClick={onBack} className="text-blue-500 text-sm p-4 hover:underline">
        &lt; Back to Auctions
      </button>
      <div className="relative">
        {/* Carousel for selected auction */}
        <img
          src={auction.images[currentImageIndex]}
          alt={auction.title}
          className="w-full h-96 object-cover"
        />
        <button
          onClick={handlePrevImage}
          className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full focus:outline-none"
        >
          &lt;
        </button>
        <button
          onClick={handleNextImage}
          className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full focus:outline-none"
        >
          &gt;
        </button>
      </div>
      <div className="p-4">
        <h3 className="text-2xl font-semibold">{auction.title}</h3>
        <p className="text-gray-700 mt-2">{auction.shortDescription}</p>
        <div className="mt-4">
          <p className="text-gray-800 font-semibold">Current Bid: ${auction.currentBid}</p>
          <p className="text-gray-500 text-sm">Auction ends: {auction.endDate}</p>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
