import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);  // Track loading state
  const [error, setError] = useState("");  // Track error message
  const navigate = useNavigate();  // Hook to navigate after login

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");  // Clear previous errors

    try {
      // Send login request to the backend
      const response = await axios.post("/login", {
        username,
        password,
      });

      // Store the JWT token in localStorage for later use
      localStorage.setItem("access_token", response.data.access_token);

      // Redirect to the dashboard or a default page after successful login
      navigate("/dashboard");
    } catch (error) {
      // Display specific error messages based on the error
      if (error.response) {
        // Server error (wrong credentials, etc.)
        setError("Invalid username or password");
      } else {
        // Network error or other issues
        setError("An error occurred. Please try again later.");
      }
    } finally {
      setLoading(false);  // Reset loading state
    }
  };

  return (
    <div className="max-w-md mx-auto">
      <form onSubmit={handleLogin} className="bg-white p-6 rounded-lg shadow-md">
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          className="block w-full p-2 border border-gray-300 rounded mb-4"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          className="block w-full p-2 border border-gray-300 rounded mb-4"
        />
        {error && <p className="text-red-500 text-sm mb-4">{error}</p>} {/* Display error message */}
        <button
          type="submit"
          className="w-full bg-blue-500 text-white p-2 rounded"
          disabled={loading}  // Disable button when loading
        >
          {loading ? "Logging in..." : "Login"}  {/* Show loading text */}
        </button>
      </form>
    </div>
  );
};

export default Login;
