import React, { useEffect, useState } from 'react';
import './index.css';
import Navbar from './components/Navbar';
import AuctionList from './components/AuctionList';
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom"; // Updated to use Routes and Route
import Registration from './components/Registration';
import Login from './components/Login';

const App = () => {
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    // Get the role from the JWT token or localStorage (this is just a placeholder)
    const token = localStorage.getItem("access_token");
    if (token) {
      const decodedToken = JSON.parse(atob(token.split(".")[1]));
      setUserRole(decodedToken.role); // Assuming role is stored in the JWT
    }
  }, []);

  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        <Navbar />
        <main className="container mx-auto p-6">
          <h1 className="text-3xl font-bold mb-6">Mnada Auctions</h1>

          <Routes>
            {/* Default route: If not logged in, show login/registration page */}
            <Route path="/" element={!userRole ? <Login /> : <Navigate to="/dashboard" />} />

            {/* Registration route */}
            <Route path="/register" element={<Registration />} />

            {/* Login route */}
            <Route path="/login" element={userRole ? <Navigate to="/dashboard" /> : <Login />} />

            {/* Dashboard route */}
            <Route path="/dashboard" element={userRole === "admin" ? <div>Admin Dashboard</div> :
                                                userRole === "auctioneer" ? <div>Auctioneer Dashboard</div> :
                                                userRole === "client" ? <div>Client Dashboard</div> :
                                                <Navigate to="/login" />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
